import React, { useState } from "react";

import ExpenseForm from "./ExpenseForm";
import "./NewExpense.css";

const NewExpense = (props) => {
  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    console.log(expenseData);
    props.onAddExpense(expenseData);
  };

  const clickHandler = () => {
    // if(form !== addExpenseButton) {
    //   setForm(<ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />)
    console.log("Clicked!");
  };

  const addExpenseButton = (
    <button onClick={clickHandler}>Add New Expense</button>
  );
  const [form, setForm] = useState(addExpenseButton);

  return <div className="new-expense">{form}</div>;
};

export default NewExpense;
